mapper.select:
SELECT a.date AS `day`, IFNULL(b.signDuration, 0) AS signDuration FROM ( SELECT DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 1 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 2 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 3 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 4 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 5 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 6 - WEEKDAY(CURDATE()) DAY) AS date ) a LEFT JOIN ( SELECT SUM(signDuration) AS signDuration, DATE_FORMAT(reportDate, '%Y-%m-%d') AS reportDate FROM record WHERE DATE_FORMAT(reportDate, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')
AND account = #{account}
GROUP BY DATE_FORMAT(reportDate, '%Y-%m-%d') ) b ON a.date = b.reportDate;

SELECT a.day, IFNULL(b.signDuration, 0) AS signDuration FROM ( SELECT DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 1 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 2 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 3 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 4 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 5 - WEEKDAY(CURDATE()) DAY) AS date UNION ALL SELECT DATE_ADD(CURDATE(), INTERVAL 6 - WEEKDAY(CURDATE()) DAY) AS date ) a LEFT JOIN ( SELECT SUM(signDuration) AS signDuration, DATE_FORMAT(reportDate, '%Y-%m-%d') AS reportDate FROM record WHERE DATE_FORMAT(reportDate, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')
AND account = #{account}
GROUP BY DATE_FORMAT(reportDate, '%Y-%m-%d') ) b ON a.day = b.reportDate;

mapper.update:
UPDATE record
SET isReminded = 1
WHERE recordId = #{recordId}

mapper.select:
SELECT COUNT(1) FROM record r LEFT JOIN users u ON r.account = u.account WHERE u.deleteFlag = 0 AND r.statusType = 1
AND r.reportDate BETWEEN #{startDate} AND #{endDate}
AND u.userName LIKE concat('%', #{userName} ,'%')
AND u.groupName = #{groupName}

SELECT u.userName, u.groupName, r.reportDate, ROUND(SUM(r.signDuration) / 60, 1) AS signDuration FROM record r LEFT JOIN users u ON r.account = u.account WHERE u.deleteFlag = 0 AND r.statusType = 1
AND r.reportDate BETWEEN #{startDate} AND #{endDate}
AND u.userName LIKE concat('%', #{userName} ,'%')
AND u.groupName = #{groupName}
GROUP BY r.reportDate, u.userName, u.groupName ORDER BY r.reportDate DESC, u.groupName, u.userName ASC
LIMIT #{size} OFFSET #{offset};
