mapper.update:
UPDATE usermeetings
SET status = 1
WHERE account = #{account}
AND meetingId = #{meetingId}

UPDATE usermeetings
SET status = 2,
    reason = #{reason}
WHERE account = #{account}
  AND meetingId = #{meetingId}

UPDATE usermeetings
SET updateDate = now(),
    checkInTime = DATE_FORMAT(NOW(), '%H:%i:%s')
WHERE account = #{account}
  AND meetingId = #{meetingId}

UPDATE usermeetings
SET updateDate = now(),
    checkOutTime = DATE_FORMAT(NOW(), '%H:%i:%s')
WHERE account = #{account}
  AND meetingId = #{meetingId}

mapper.select:
SELECT DISTINCT m.reportDate
FROM meetings m
JOIN usermeetings um ON m.meetingId = um.meetingId
WHERE YEAR(m.reportDate) = #{year}
  AND MONTH(m.reportDate) = #{month}
  AND um.status IS NOT NULL
  AND um.account = #{account}
